(()=>{"use strict";!function(){const t=window.getSelection();if(t){const e=t.toString();e&&fetch(`https://en.wikipedia.org/api/rest_v1/page/summary/${e}`).then((t=>t.json())).then((t=>{chrome.runtime.sendMessage({wikipediaData:t})})).catch((t=>{console.error("Error fetching data from Wikipedia API in contentScript:",t)}))}}()})();